import { useState } from "react";
import "./App.css";
function App() {
  // let counter = 15;
  let [counter, setCounter] = useState(0);
  const addValue = () => {
    setCounter(counter + 1);
  };

  const removeValue = () => {
    setCounter(counter - 1);
    if (counter <= 0) {
      setCounter(0);
    } else {
      setCounter(--counter);
    }
  };

  return (
    <>
      <div className="main">
        <h2> counter value {counter}</h2>
        <br />
        <div className="sub-box">
          <button onClick={addValue}>+</button>
          <button onClick={removeValue}>-</button>
        </div>

        <br></br>
      </div>
    </>
  );
}

export default App;
